library(shiny)

mpg.data <- function(am,wt,qsec){
  return(mpg)
}

shinyServer(
  function(input, output) {
    
    output$text1 <- renderText({ 
      paste("You have selected transmission type as", input$am)
    })
    
    output$text2 <- renderText({ 
      paste("You have entered car's weight as ", input$wt,"(lb/1000)" )
    })
    
    output$text3 <- renderText({ 
      paste("You have set the quarter mile time to be", input$qsec)
    })
    
    output$text4 <- renderText({ 
      if (input$action) {
        mpg <- 9.617781 - 3.916504*input$wt + 1.225886*input$qsec
        if (input$am=="Manual"){
          mpg <- mpg + 2.935837
        }
        paste("Estimated mpg is", mpg)
      }
    })
    
    output$text5 <- renderText({ 
      if (input$carImg) {
        paste("An example", input$am, "transmission car")
      }
    })
    
    
    output$cartype <- renderUI({
      if (input$carImg) {
        images <- c("data:image/jpeg;base64,", 
                    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS3hZf_D3NO5QJuRwDqJAdwDR1y3x90EyL_0Fr9y5knxlzjaizpkQ")
        
        if (input$am=="Manual"){
          a <- 1
        }
        if (input$am=="Automatic"){
          a <- 2
        }
        tags$img(src= images[a])
      }
    })
    
  }
)
